import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
@Injectable({
  providedIn: 'root'
})
export class UserService {

  token = '530d9dc1b99da7bde5ca7e33b414b14fd27437f623208b70504cbf060a3641db';
  baseUrl = 'https://gorest.co.in/public/v2/';

   headers = new HttpHeaders({
    'Content-Type': 'application/json',
    'auth': this.token

  })
  constructor(private http: HttpClient) { }



  getAllUsers() {
    return this.http.get(this.baseUrl + "users" , { headers: this.headers });
  }

  addProduct(body:any) {
    return this.http.post(this.baseUrl + "products",body);
  }

  editProduct(body:any,productId:any) {
    return this.http.put(this.baseUrl + "products/" + productId,body);
  }
  deleteProduct(productId:any) {

    return this.http.delete(this.baseUrl + "products/" + productId);
  }

  getAllCategories() {
    return this.http.get(this.baseUrl + "products/categories");
  }
}
