import { OnInit } from '@angular/core';
import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { UserService } from '../user.service';

@Component({
  selector: 'app-posts',
  templateUrl: './posts.component.html',
  styleUrls: ['./posts.component.scss']
})
export class PostsComponent implements OnInit {
  userIdShoot:any;
  displayedColumns;
  dataSource;
  showDeleteSuccess = false;
  constructor( private actroute: ActivatedRoute,private userServ: UserService, private route: Router) { }
  ngOnInit(): void {
    this.userIdShoot = this.actroute.snapshot.params["id"];
    console.log(this.userIdShoot);
    this.getPosts()

  }


  getPosts() {
    this.userServ.getUserPosts(this.userIdShoot).subscribe({
      next: (response) => {
        console.log(response);
        this.displayedColumns = ['title', 'body','delete'];
        this.dataSource = response;
      },
      error: (e) => {
        console.log('no users exist');
      }
    }

    )
  }


  deletePost(PostId) {
    this.userServ.deleteUserPost(this.userIdShoot,PostId).subscribe({
      next: (response) => {
        console.log("deleted");
        this.showDeleteSuccess = true;

        setTimeout(() => {
          this.showDeleteSuccess = false;
          this.getPosts()
        }, 1500);

      },
      error: (e) => {
        console.log('error in delete');
      }


    }

    )

  }
}
